<?php	class DataBase {		public static function connect () {			$servername = "localhost";			$username = "sql_myhost";			$password = "czy1303515";			$dbname = "sql_myhost";			// 创建连接			$conn = new mysqli($servername, $username, $password, $dbname);			$conn->set_charset("utf8");			// 检测连接			if ($conn->connect_error) {				die("Connection failed: " . $conn->connect_error);			} else {				return $conn;			}		}		public static function find($tableName, $attr, $value) {			$conn = self::connect();			$sql = "SELECT * FROM `{$tableName}` WHERE `{$attr}` = $value";			$result = $conn->query($sql);			$sentencesData = array();			$res = array();			if ($result->num_rows > 0) {				// 输出每行数据				while($row = $result->fetch_assoc()) {					$res[] = $row;				}			}			$conn->close();			return $res;		}	}